  procedure change_parent_in_division(p_id in varchar2, p_parent_id in varchar2)
  as
  div_rec divisions%rowtype;
  begin
  select * into div_rec from divisions where id = p_id for update;
  div_rec.parent_id:=p_parent_id;
  update divisions
    set row = div_rec
  where id = p_id;
  exception
  when others then
    raise;
  end;
  
